mapper.update:

update phy_symptoms_type
set SYMPTOMS_PRI = #{symptomsPri,jdbcType=INTEGER},
  SYMPTOMS_NAME = #{symptomsName,jdbcType=VARCHAR},
  SYMPTOMS_TYPE = #{symptomsType,jdbcType=VARCHAR},
  PHY_BELONG = #{phyBelong,jdbcType=INTEGER}
where SYMPTOMS_SEQ = #{symptomsSeq,jdbcType=INTEGER}

mapper.select:

select SYMPTOMS_SEQ, SYMPTOMS_PRI, SYMPTOMS_NAME, SYMPTOMS_TYPE, PHY_BELONG 
from phy_symptoms_type
where SYMPTOMS_SEQ = #{symptomsSeq,jdbcType=INTEGER}
